-- DB 생성   
DROP DATABASE IF EXISTS pizzaitho;
                                                                                                                                                                                                                     
  CREATE DATABASE IF NOT EXISTS pizzaitho                                                                                                                                                                                          
    DEFAULT CHARACTER SET utf8mb4                                                                                                                                                                                                   
    COLLATE utf8mb4_general_ci;                                                                                                                                                                                                     
  USE pizzaitho;                                                                                                                                                                                                                   
                                                                                                                                                                                                                                    
  -- USER                                                                                                                                                                                                                           
  CREATE TABLE `USER` (                                                                                                                                                                                                             
    user_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                         
    id VARCHAR(50) NOT NULL UNIQUE,                                                                                                                                                                                                 
    pw VARCHAR(255) NOT NULL,                                                                                                                                                                                                       
    name VARCHAR(50) NOT NULL,                                                                                                                                                                                                      
    stamp INT NOT NULL DEFAULT 0                                                                                                                                                                                                    
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- PRODUCT                                                                                                                                                                                                                        
  CREATE TABLE PRODUCT (                                                                                                                                                                                                            
    product_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                      
    name VARCHAR(100) NOT NULL,                                                                                                                                                                                                     
    description TEXT,                                                                                                                                                                                                               
    image VARCHAR(255),                                                                                                                                                                                                             
    base_price INT NOT NULL                                                                                                                                                                                                         
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- TOPPING                                                                                                                                                                                                                        
  CREATE TABLE TOPPING (                                                                                                                                                                                                            
    topping_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                      
    name VARCHAR(100) NOT NULL,                                                                                                                                                                                                     
    image VARCHAR(255),                                                                                                                                                                                                             
    price INT NOT NULL                                                                                                                                                                                                              
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
																																																								
  -- DOUGH                                                                                                                                                                                                                          
  CREATE TABLE DOUGH (                                                                                                                                                                                                              
    dough_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                        
    name VARCHAR(100) NOT NULL,                                                                                                                                                                                                     
    image VARCHAR(255),                                                                                                                                                                                                             
    extra_price INT NOT NULL                                                                                                                                                                                                        
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- CRUST                                                                                                                                                                                                                          
  CREATE TABLE CRUST (                                                                                                                                                                                                              
    crust_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                        
    name VARCHAR(100) NOT NULL,                                                                                                                                                                                                     
    image VARCHAR(255),                                                                                                                                                                                                             
    extra_price INT NOT NULL                                                                                                                                                                                                        
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- PRODUCT_DEFAULT_TOPPING
  CREATE TABLE PRODUCT_DEFAULT_TOPPING (                                                                                                                                                                                            
    product_id INT NOT NULL,                                                                                                                                                                                                        
    topping_id INT NOT NULL,                                                                                                                                                                                                        
    PRIMARY KEY (product_id, topping_id),                                                                                                                                                                                           
    CONSTRAINT fk_pdt_product                                                                                                                                                                                                       
      FOREIGN KEY (product_id) REFERENCES PRODUCT(product_id),                                                                                                                                                                      
    CONSTRAINT fk_pdt_topping                                                                                                                                                                                                       
      FOREIGN KEY (topping_id) REFERENCES TOPPING(topping_id)                                                                                                                                                                       
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- ORDERS                                                                                                                                                                                                                         
  CREATE TABLE ORDERS (                                                                                                                                                                                                             
    order_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                        
    user_id INT NOT NULL,                                                                                                                                                                                                           
    order_table VARCHAR(100),                                                                                                                                                                                                       
    status VARCHAR(20) NOT NULL,                                                                                                                                                                                                    
    fcm_token VARCHAR(255),                                                                                                                                                                                                         
    order_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP,                                                                                                                                                                                 
    CONSTRAINT fk_orders_user                                                                                                                                                                                                       
      FOREIGN KEY (user_id) REFERENCES `USER`(user_id)                                                                                                                                                                              
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- ORDER_DETAIL                                                                                                                                                                                                                   
  CREATE TABLE ORDER_DETAIL (                                                                                                                                                                                                       
    order_detail_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                 
    order_id INT NOT NULL,                                                                                                                                                                                                          
    product_id INT NOT NULL,
    dough_id INT NOT NULL,                                                                                                                                                                                                          
    crust_id INT,                                                                                                                                                                                                                   
    quantity INT NOT NULL DEFAULT 1,                                                                                                                                                                                                
    unit_price INT NOT NULL,                                                                                                                                                                                                        
    CONSTRAINT fk_od_order                                                                                                                                                                                                          
      FOREIGN KEY (order_id) REFERENCES ORDERS(order_id),                                                                                                                                                                           
    CONSTRAINT fk_od_product                                                                                                                                                                                                        
      FOREIGN KEY (product_id) REFERENCES PRODUCT(product_id),                                                                                                                                                                      
    CONSTRAINT fk_od_dough                                                                                                                                                                                                          
      FOREIGN KEY (dough_id) REFERENCES DOUGH(dough_id),                                                                                                                                                                            
    CONSTRAINT fk_od_crust                                                                                                                                                                                                          
      FOREIGN KEY (crust_id) REFERENCES CRUST(crust_id)                                                                                                                                                                             
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- ORDER_DETAIL_TOPPING                                                                                                                                                                                                           
  CREATE TABLE ORDER_DETAIL_TOPPING (                                                                                                                                                                                               
    order_detail_id INT NOT NULL,                                                                                                                                                                                                   
    topping_id INT NOT NULL,                                                                                                                                                                                                        
    quantity INT NOT NULL DEFAULT 1,                                                                                                                                                                                                
    PRIMARY KEY (order_detail_id, topping_id),                                                                                                                                                                                      
    CONSTRAINT fk_odt_order_detail                                                                                                                                                                                                  
      FOREIGN KEY (order_detail_id) REFERENCES ORDER_DETAIL(order_detail_id),                                                                                                                                                       
    CONSTRAINT fk_odt_topping                                                                                                                                                                                                       
      FOREIGN KEY (topping_id) REFERENCES TOPPING(topping_id)                                                                                                                                                                       
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- COMMENT                                                                                                                                                                                                                        
  CREATE TABLE `COMMENT` (                                                                                                                                                                                                          
    comment_id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                      
    user_id INT NOT NULL,                                                                                                                                                                                                           
    product_id INT NOT NULL,                                                                                                                                                                                                        
    order_detail_id INT NOT NULL,                                                                                                                                                                                                   
    rating INT NOT NULL,                                                                                                                                                                                                            
    comment TEXT NOT NULL,                                                                                                                                                                                                          
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,                                                                                                                                                                                 
    CONSTRAINT fk_comment_user                                                                                                                                                                                                      
      FOREIGN KEY (user_id) REFERENCES `USER`(user_id),                                                                                                                                                                             
    CONSTRAINT fk_comment_product                                                                                                                                                                                                   
      FOREIGN KEY (product_id) REFERENCES PRODUCT(product_id),                                                                                                                                                                      
    CONSTRAINT fk_comment_order_detail                                                                                                                                                                                              
      FOREIGN KEY (order_detail_id) REFERENCES ORDER_DETAIL(order_detail_id)                                                                                                                                                        
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;                                                                                                                                                                                          
                                                                                                                                                                                                                                    
  -- STAMP LOG                                                                                                                                                                                                                      
   CREATE TABLE t_stamp (                                                                                                                                                                                                            
    id INT AUTO_INCREMENT PRIMARY KEY,                                                                                                                                                                                              
    user_id INT NOT NULL,                                                                                                                                                                                                           
    order_id INT NOT NULL,                                                                                                                                                                                                          
    quantity INT NOT NULL,                                                                                                                                                                                                          
    CONSTRAINT fk_stamp_user                                                                                                                                                                                                        
      FOREIGN KEY (user_id) REFERENCES `USER`(user_id),                                                                                                                                                                             
    CONSTRAINT fk_stamp_order                                                                                                                                                                                                       
      FOREIGN KEY (order_id) REFERENCES ORDERS(order_id)                                                                                                                                                                            
  ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4; 
  
