  INSERT INTO `COMMENT` (user_id, product_id, order_detail_id, rating, comment) VALUES                                                                                                                                              
  -- user_id = 1                                                                                                                                                                                                                    
  (1, 1, (SELECT od.order_detail_id FROM ORDER_DETAIL od JOIN ORDERS o ON o.order_id = od.order_id WHERE o.user_id = 1 AND od.product_id = 1 ORDER BY RAND() LIMIT 1), 5, '치즈 듬뿍이라 만족! 고구마 크러스트랑 조합 최고였어요.'),
  (1, 3, (SELECT od.order_detail_id FROM ORDER_DETAIL od JOIN ORDERS o ON o.order_id = od.order_id WHERE o.user_id = 1 AND od.product_id = 3 ORDER BY RAND() LIMIT 1), 4, '페퍼로니가 넉넉해서 좋았고 도우도 바삭했어요.'),         
  (1, 2, (SELECT od.order_detail_id FROM ORDER_DETAIL od JOIN ORDERS o ON o.order_id = od.order_id WHERE o.user_id = 1 AND od.product_id = 2 ORDER BY RAND() LIMIT 1), 5, '불고기 양념이 달달해서 가족이 좋아했어요.'),             
  (1, 4, (SELECT od.order_detail_id FROM ORDER_DETAIL od JOIN ORDERS o ON o.order_id = od.order_id WHERE o.user_id = 1 AND od.product_id = 4 ORDER BY RAND() LIMIT 1), 3, '무난한 맛! 토핑 더 추가하면 더 맛있을 듯.');             
                                                                                                                                                                                                                                    
  -- user_id = 2                                                                                                                                                                                                                    
